//import { useState } from "react"

const cuisine = [{id: 1, name: "American"}, {id: 2, name: "Chinese"}, {id: 3, name: "Italian"},{id: 4, name: "Middle Eastern"}, {id: 5, name: "Fast Food"}]
const protein = [{id: 1, name:"Meat"}, {id: 2, name:"Chicken"}]
export default function RecommendationSelection() {
    return(
        <div className="recommendation">
            <h1>Recommendation</h1>
            <p>Pick one from each row</p>
        <ul>
            <h3>Cuisine</h3>
                <ul className="options">
                    {cuisine.map((val, key) => {
                        return(
                           <li key={val.id}>{val.name}</li>
                        )
                    })}
                </ul>
            <h3>Protein</h3>
                <ul className="options">
                    {protein.map((val, key) => {
                        return(
                            <li key={val.id}>{val.name}</li>
                        )
                    })}
                </ul>
            <h3>Location</h3>    
                <ul className="options">
                    <li>Baker Street</li>
                    <li>Leicester Square</li>
                    <li>London Bridge</li>
                    <li>China Town</li>
                    <li>Camden Market</li>
                </ul>
        </ul>
        <br />
        <div>
            <h4>Ooooo very nice, based on your preference you should check out:</h4>
        </div>
        </div>
    ) 
}

