import React from "react";
import "../src/main.scss";

const feed = [
  {
    title: "Bill or Beak",
    alcohol: false,
    fullHalal: true,
    prayer_area: false,
    disableFriendly: true,
    cuisine: "American",
  },
  {
    title: "Chicken Shop",
    alcohol: true,
    fullHalal: true,
    prayer_area: false,
    disableFriendly: true,
    cuisine: "Fast Food",
  },
  {
    title: "ShakeShuka",
    alcohol: false,
    fullHalal: true,
    prayer_area: true,
    disableFriendly: false,
    cuisine: "Middle Eastern",
  },

];

export default class Hello extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      filterFinish: "",
      filterDiameter: "",
      filterWidth: "",
      filterBoltPattern: "",
      filterStyle: "",
      filterOffset: "",
      filterPrice: "",
      sortingOrder: "default",
      priceLowToHigh: "",
      priceHighToLow: "",
      isListView: false,
      totalCount: ""
    };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.filterWheels.bind(this);
    //this.handleClick = this.handleListView.bind(this);
  }

  filterWheels(el) {
    console.log("clicking filter item");

    console.log(el.currentTarget);

    let value = el.currentTarget.dataset.value;
    let key = el.currentTarget.dataset.key;

    console.log("value=" + value);
    console.log("key=" + key);

    if (key === "finish") {
      this.setState((prevState) => ({
        filterFinish: this.state.filterFinish === value ? null : value
      }));
    }
    if (key === "price") {
      this.setState((prevState) => ({
        filterPrice: this.state.filterPrice === value ? null : value
      }));
    }
    if (key === "style") {
      this.setState((prevState) => ({
        filterStyle: this.state.filterStyle === value ? null : value
      }));
    }
    if (key === "diameter") {
      this.setState((prevState) => ({
        filterDiameter: this.state.filterDiameter === value ? null : value
      }));
    }
    if (key === "width") {
      this.setState((prevState) => ({
        filterWidth: this.state.filterWidth === value ? null : value
      }));
    }
    if (key === "bolt-pattern") {
      this.setState((prevState) => ({
        filterboltPattern: this.state.filterboltPattern === value ? null : value
      }));
    }
    if (key === "offset") {
      this.setState((prevState) => ({
        filterOffset: this.state.filterOffset === value ? null : value
      }));
    }
  }

  sortedProduct(el) {
    this.setState({
      sortingOrder: el.target.value
    });
  }

  sortedPrice(el) {
    this.setState({});
  }

  handleListView(el) {
    console.log("clicking list view");
    console.log(el);
    if (this.state.isListView) {
      this.setState({ isListView: false });
    } else {
      this.setState({ isListView: true });
    }
  }

  handleTotalCount(el) {
    let count = 0;
    this.setState({
      totalCount: count
    });
  }

  render() {
    let state = this.state;

    let renderListViewItem = (wheel) => (
      <div class="product-list">
        <a href="https://reikavs.com/shop/wheels/R15570151007F/">
          <span class="list-thumbnail-wrapper">
            <img class="list-image" src={wheel.image} />
          </span>
          <span class="list-product-card-text-wrapper">
            <div class="list-title">{wheel.title}</div>
            <div class="list-subtitle">
              {wheel.size} | {wheel.offset} | {wheel.boltPattern} |{" "}
              {wheel.finish}
            </div>
            <div class="list-price">${wheel.price}</div>
          </span>
        </a>
      </div>
    );

    // <div class="column-half">
    //   <img class="top-picture" src="images/software.svg" alt="smart-software">
    //   <div class="text-content">
    //     <h3>Intuitive, <br>smart software</h3>
    //     <p>We build products that transform how healthcare facilities run across Africa - making them
    //     radically more efficient operationally and financially, a complete turnaround.
    //     </p>
    //   </div>
    // </div>

    let renderGridViewItem = (wheel) => (
      <div class="product">
        <a href="https://reikavs.com/shop/wheels/R15570151007F/">
          <span class="thumbnail-wrapper">
            <img src={wheel.image} />
          </span>
          <span class="product-card-text-wrapper">
            <span class="product-title">{wheel.title}</span>
            <span class="product-subtitle">
              {wheel.size} | {wheel.offset} | {wheel.boltPattern} |{" "}
              {wheel.finish}
            </span>
            <span class="product-price">${wheel.price}</span>
          </span>
        </a>
      </div>
    );

    let renderItem = renderGridViewItem;
    if (this.state.isListView) {
      renderItem = renderListViewItem;
    }

    let filteredFeed = feed.filter(function (wheel) {
      var passesPriceCheck = false;
      if (state.filterPrice === "$0 - $50" && wheel.price < 50) {
        passesPriceCheck = true;
      } else if (
        state.filterPrice === "$50 - $100" &&
        wheel.price > 50 &&
        wheel.price < 100
      ) {
        passesPriceCheck = true;
      } else if (
        state.filterPrice === "$100 - $150" &&
        wheel.price > 100 &&
        wheel.price < 150
      ) {
        passesPriceCheck = true;
      } else if (
        state.filterPrice === "$150 - $200" &&
        wheel.price > 150 &&
        wheel.price < 200
      ) {
        passesPriceCheck = true;
      } else if (
        state.filterPrice === "$200 - $250" &&
        wheel.price >= 200 &&
        wheel.price < 250
      ) {
        passesPriceCheck = true;
      }

      return (
        (!state.filterStyle || state.filterStyle === wheel.style) &&
        (!state.filterDiameter || state.filterDiameter === wheel.diameter) &&
        (!state.filterWidth || state.filterWidth === wheel.width) &&
        (!state.filterBoltPattern ||
          state.filterBoltPattern === wheel.boltPattern) &&
        (!state.filterFinish || state.filterFinish === wheel.finish) &&
        (!state.filterOffset || state.filterOffset === wheel.offset) &&
        (!state.filterPrice || passesPriceCheck) //compare range //if ()
      );
    });

    filteredFeed = filteredFeed.sort((a, b) => {
      if (state.sortingOrder === "most expensive product first") {
        return b.price - a.price;
      } else if (state.sortingOrder === "newest product first") {
        return a.created_date > b.created_date;
      } else if (state.sortingOrder === "least expensive product first") {
        return a.price - b.price;
      }
    });

    let totalResult = filteredFeed.length;

    let listItems = filteredFeed.map((wheel) => renderItem(wheel));

    return (
      <div className={this.state.isListView ? "list-view" : ""}>
        <div class="product-filter-wrapper m10-filters">
          <div class="product-filter-right">
            <h1>Shop wheels</h1>
            <div class="black-line-right"></div>
            <div class="view d-flex align-items-center justify-content-end">
              <div class="d-flex view-left">
                <p class="totalResult"> Showing {totalResult} result </p>
              </div>
              <div class="view-right d-flex">
                <div>
                  <i
                    class="icon fa-solid fa-list"
                    onClick={(e) => this.handleListView(e)}
                  ></i>
                  <i
                    class="icon fa-solid fa-grip"
                    onClick={(e) => this.handleListView(e)}
                  ></i>
                </div>
                <div class="dropDown">
                  <form>
                    <label for="dropDown"></label>
                    <div class="sortingDropDown">
                      <select
                        name="sorting"
                        id="sorting"
                        onChange={(e) => this.sortedProduct(e)}
                        class="form-select form-select-sm"
                        aria-label=".form-select-sm example"
                      >
                        <option selected>Default Sorting</option>
                        <option value="newest product first">
                          Sort by latest
                        </option>
                        <option value="least expensive product first">
                          Sort by price: low to high
                        </option>
                        <option value="most expensive product first">
                          Sort by price: high to low
                        </option>
                      </select>
                    </div>
                  </form>
                </div>
              </div>
            </div>
            <div class="black-line-right"></div>
            <div class="products-list">{listItems}</div>
          </div>

          <div class="product-filter-left m10-filters-sticky">
            <h2>Filter</h2>
            <div class="black-line"></div>
            <h3 class="style">Services</h3>
            <ul class="filter-style">
              <li
                data-value="Alcohol"
                data-key="Alcohol"
                className={
                  this.state.filterStyle === true ? "selected" : ""
                }
                onClick={(e) => this.filterWheels(e)}
              >
                <i class="far fa-square checkbox-open"></i>
                <i class="fas fa-check-square checkbox-closed"></i>
                &nbsp;Detour R25
              </li>
              <li
                data-value="Seeker R15"
                data-key="style"
                className={
                  this.state.filterStyle === "Seeker R15" ? "selected" : ""
                }
                onClick={(e) => this.filterWheels(e)}
              >
                <i class="far fa-square checkbox-open"></i>
                <i class="fas fa-check-square checkbox-closed"></i>
                &nbsp;Seeker R15
              </li>
              <li
                data-value="Teton R20"
                data-key="style"
                className={
                  this.state.filterStyle === "Teton R20" ? "selected" : ""
                }
                onClick={(e) => this.filterWheels(e)}
              >
                <i class="far fa-square checkbox-open"></i>
                <i class="fas fa-check-square checkbox-closed"></i>
                &nbsp;Teton R20
              </li>
