import React, { useState } from 'react';

const restaurants = [
    { id: 1, name: 'Italian Delight', cuisine: 'Italian', location: 'Downtown', price: 'Moderate', rating: 4.5, open: true },
    { id: 2, name: 'Sushi House', cuisine: 'Japanese', location: 'Nearby', price: 'Expensive', rating: 4.2, open: false },
  ];

export default function RestaurantSearch() {
  const [filters, setFilters] = useState({
    cuisine: [],
    location: 'Downtown',
    price: [],
    dietaryRestrictions: [],
    rating: 4.0,
  });

  const applyFilters = restaurant => {
    const { cuisine, location, price, dietaryRestrictions, rating } = filters;

    const cuisineMatch = cuisine.length === 0 || cuisine.includes(restaurant.cuisine);
    const locationMatch = restaurant.location === location;
    const priceMatch = price.length === 0 || price.includes(restaurant.price);
    const dietaryRestrictionsMatch =
      dietaryRestrictions.length === 0 ||
      dietaryRestrictions.some(d => restaurant.dietaryRestrictions.includes(d));
    const ratingMatch = restaurant.rating >= rating;

    return cuisineMatch && locationMatch && priceMatch && dietaryRestrictionsMatch && ratingMatch;
  };

  const filteredRestaurants = restaurants.filter(applyFilters);

  return (
    <div>
      <h1>Restaurant Search</h1>
      {/* Filter components */}
      <select onChange={e => setFilters({ ...filters, cuisine: e.target.value })}>
        <option value="All">All Cuisines</option>
        <option value="Italian">Italian</option>
        <option value="Japanese">Japanese</option>
        {/* ...other cuisine options */}
      </select>
      {/* Similar filter components for location, price, rating, and open status */}

      {/* Display filtered restaurants */}
      <ul>
        {filteredRestaurants.map(restaurant => (
          <li key={restaurant.id}>
            {restaurant.name} - {restaurant.cuisine} - {restaurant.location}
          </li>
        ))}
      </ul>
    </div>
  );
};
