import React, { useState } from "react"; // Import useState if needed

export default function RecommendationSelection() {
  // Define a state to track the selected options
  const [selectedOptions, setSelectedOptions] = useState({
    cuisine: true,
    protein: true,
    location: true,
  });

  // Function to handle option selection
  const handleOptionClick = (category, option) => {
    setSelectedOptions((prevState) => ({
      ...prevState,
      [category]: option,
    }));
  };

  return (
    <div className="recommendation">
      <h1>Recommendation</h1>
      <p>Pick one from each row</p>
      <ul>
        <h3>Cuisine</h3>
        <ul className="options">
          <li onClick={(selectedOptions) => handleOptionClick("cuisine", "American")}>
            American
          </li>
          <li onClick={() => handleOptionClick("cuisine", "Chinese")}>
            Chinese
          </li>
          <li onClick={() => handleOptionClick("cuisine", "Fast Food")}>
            Fast Food
          </li>
          <li onClick={() => handleOptionClick("cuisine", "Middle Eastern")}>
            Middle Eastern
          </li>
          {/* ...other options */}
        </ul>
        <h3>Protein</h3>
        <ul className="options">
          <li onClick={() => handleOptionClick("protein", "Meat")}>Meat</li>
          <li onClick={() => handleOptionClick("protein", "Chicken")}>
            Chicken
          </li>
          {/* ...other options */}
        </ul>
        <h3>Location</h3>
        <ul className="options">
          <li onClick={() => handleOptionClick("location", "Baker Street")}>
            Baker Street
          </li>
          <li onClick={() => handleOptionClick("location", "Leicester Square")}>
            Leicester Square
          </li>
          {/* ...other options */}
        </ul>
      </ul>
      {/* ...display the selected recommendation */}
    </div>
  );
}
